import React from 'react';

function ChatList({ onlineUsers, currentChat, setCurrentChat, username }) {
  const userIds = Array.from(onlineUsers).filter((id) => id !== localStorage.getItem('userId'));

  return (
    <div className="chat-list">
      <h2>Chats</h2>
      {userIds.length > 0 ? (
        userIds.map((userId) => (
          <div
            key={userId}
            onClick={() => setCurrentChat({ _id: userId, username: userId })} // Placeholder username
            className={`${currentChat && currentChat._id === userId ? 'active' : ''}`}
          >
            <span>{userId}</span>
            <span> (Online)</span>
          </div>
        ))
      ) : (
        <p>No users online</p>
      )}
    </div>
  );
}

export default ChatList;
