import axios from 'axios';


const API = axios.create({ baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api' });


export const login = (username) => API.post('/auth/login', { username }).then(r => r.data);
export const getUsers = () => API.get('/auth/users').then(r => r.data.users);
export const getMessages = (userId, otherId) => API.get(`/messages/${userId}/${otherId}`).then(r => r.data.messages);
export const postMessage = (payload) => API.post('/messages', payload).then(r => r.data.message);


export default API;
