import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import ChatList from './components/ChatList';
import ChatWindow from './components/ChatWindow';
import './styles.css';

const socket = io('http://localhost:5000');

function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [onlineUsers, setOnlineUsers] = useState([]); // Store user objects
  const [currentChat, setCurrentChat] = useState(null);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket.on('connect', () => {
      console.log('Connected to server:', socket.id);
    });

    socket.on('update:users', (userIds) => {
      const fetchUsers = async () => {
        const users = await Promise.all(
          userIds.map(async (id) => {
            const response = await fetch(`http://localhost:5000/api/auth/user/${id}`);
            const data = await response.json();
            return data.user || { _id: id, username: id }; // Fallback
          })
        );
        setOnlineUsers(users);
      };
      fetchUsers();
    });

    socket.on('receive:message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleLogin = async () => {
    if (username.trim()) {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username }),
      });
      const data = await response.json();
      if (data.user) {
        localStorage.setItem('userId', data.user._id);
        socket.emit('user:connect', data.user);
        setIsLoggedIn(true);
      }
    }
  };

  const sendMessage = (text) => {
    if (!text.trim() || !currentChat || !currentChat._id) {
      console.log('Cannot send message: Invalid input or chat not selected', { text, currentChat });
      return;
    }
    const msg = {
      sender: localStorage.getItem('userId'),
      receiver: currentChat._id,
      text,
      timestamp: new Date(),
    };
    console.log('Sending message:', msg); // Debug log
    socket.emit('send:message', msg, (response) => {
      if (response.error) {
        console.error('Server error:', response.error);
      } else {
        setMessages((prev) => [...prev, { ...msg, sender: { username }, receiver: currentChat }]);
      }
    });
  };

  if (!isLoggedIn) {
    return (
      <div className="login-container">
        <div className="login-box">
          <h1>Login</h1>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <ChatList
        onlineUsers={onlineUsers}
        currentChat={currentChat}
        setCurrentChat={setCurrentChat}
      />
      <ChatWindow
        currentChat={currentChat}
        messages={messages}
        sendMessage={sendMessage}
        username={username}
      />
    </div>
  );
}

export default App;
