import React from 'react';

export default function ChatList({ users, onlineUsers, onOpen }) {
  return (
    <ul className="chat-list">
      {users.map((u) => (
        <li
          key={u._id}
          onClick={() => onOpen(u)}
          className={onlineUsers.has(u._id) ? 'online' : 'offline'}
        >
          {u.username} {onlineUsers.has(u._id) ? '(Online)' : '(Offline)'}
        </li>
      ))}
    </ul>
  );
}
