import React, { useState, useEffect } from 'react';

export default function ChatWindow({ user, other, socket }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');

  useEffect(() => {
    const handler = (e) => {
      const msg = e.detail;
      if (msg.sender === other.id || msg.receiver === other.id) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    window.addEventListener('socket:message', handler);
    return () => window.removeEventListener('socket:message', handler);
  }, [other.id]);

  const sendMessage = () => {
    if (!text) return;
    const msg = { sender: user.id, receiver: other.id, text, timestamp: new Date() };
    socket.emit('send:message', msg);
    setMessages((prev) => [...prev, msg]);
    setText('');
  };

  return (
    <div className="chat-window">
      <h2>Chat with {other.username}</h2>
      <div className="messages">
        {messages.map((m, idx) => (
          <div key={idx} className={m.sender === user.id ? 'my-msg' : 'their-msg'}>
            {m.text}
          </div>
        ))}
      </div>
      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Type a message"
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
}
