import React, { useState, useEffect } from 'react'; // Removed unused useRef
import io from 'socket.io-client';
import ChatList from './components/ChatList';
import ChatWindow from './components/ChatWindow';
import './styles.css';

const socket = io('http://localhost:5000');

function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [onlineUsers, setOnlineUsers] = useState(new Set());
  const [currentChat, setCurrentChat] = useState(null);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket.on('connect', () => {
      console.log('Connected to server:', socket.id);
    });

    socket.on('update:users', (userIds) => {
      setOnlineUsers(new Set(userIds));
    });

    socket.on('receive:message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleLogin = async () => {
    if (username.trim()) {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username }),
      });
      const data = await response.json();
      if (data.user) {
        socket.emit('user:connect', data.user);
        setIsLoggedIn(true);
      }
    }
  };

  const sendMessage = (text) => {
    if (text.trim() && currentChat) {
      const msg = {
        sender: localStorage.getItem('userId'),
        receiver: currentChat._id,
        text,
        timestamp: new Date(),
      };
      socket.emit('send:message', msg);
      setMessages((prev) => [...prev, { ...msg, sender: { username }, receiver: currentChat }]);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="login-container">
        <div className="login-box">
          <h1>Login</h1>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <ChatList
        onlineUsers={onlineUsers}
        currentChat={currentChat}
        setCurrentChat={setCurrentChat}
        username={username}
      />
      <ChatWindow
        currentChat={currentChat}
        messages={messages}
        sendMessage={sendMessage}
      />
    </div>
  );
}

export default App;
