import React, { useState, useEffect, useRef } from 'react';
import { getMessages } from '../services/api';

export default function ChatWindow({ user, other, socket }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (!other) return;
    const fetchMessages = async () => {
      try {
        const fetchedMessages = await getMessages(other._id, user._id);
        setMessages(fetchedMessages);
      } catch (err) {
        console.error('Fetch messages error:', err);
      }
    };
    fetchMessages();
  }, [other, user._id]);

  useEffect(() => {
    const handler = (e) => {
      const msg = e.detail;
      if (msg.sender._id === other._id || msg.receiver._id === other._id) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    window.addEventListener('socket:message', handler);
    return () => window.removeEventListener('socket:message', handler);
  }, [other._id]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    if (!text.trim()) return;
    const msg = { sender: user._id, receiver: other._id, text, timestamp: new Date() };
    socket.emit('send:message', msg);
    setText('');
  };

  return (
    <div className="chat-window">
      <h2>Chat with {other.username}</h2>
      <div className="messages">
        {messages.map((m, idx) => (
          <div
            key={idx}
            className={m.sender._id === user._id ? 'my-msg' : 'their-msg'}
          >
            <strong>{m.sender._id === user._id ? 'You' : other.username}: </strong>
            {m.text} <small>({new Date(m.timestamp).toLocaleTimeString()})</small>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Type a message"
        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
}
